package com.example.taskconvertaiapp.shared.ui.screens.auth

import android.content.Context
import androidx.datastore.core.DataStore
import androidx.datastore.preferences.core.Preferences
import androidx.datastore.preferences.preferencesDataStore
import com.example.taskconvertaiapp.shared.data.DefaultAppContainer
import com.example.taskconvertaiapp.shared.data.auth.AuthRepository

// DataStore setup
private const val AUTH_PREFERENCES = "auth_preferences"
private val Context.dataStore: DataStore<Preferences> by preferencesDataStore(
    name = AUTH_PREFERENCES
)

// Global application context holder
private lateinit var applicationContext: Context

// Initialize function to be called from Android Application class
fun initializeAuthRepository(context: Context) {
    applicationContext = context.applicationContext
}

// Actual implementation for Android
actual fun provideAuthRepository(): AuthRepository {
    val container = DefaultAppContainer(applicationContext.dataStore)
    return container.authRepository
}

package com.example.taskconvertaiapp.shared.ui.screens.auth

import com.example.taskconvertaiapp.shared.data.auth.AuthRepository
import com.example.taskconvertaiapp.shared.data.auth.UserAuthPreferencesRepository
import com.example.taskconvertaiapp.shared.data.createAuthRepository
import androidx.datastore.core.DataStore
import androidx.datastore.preferences.core.Preferences

// Platform-specific DataStore instance
private lateinit var dataStoreInstance: DataStore<Preferences>

// Initialize function to be called from iOS
fun initializeAuthRepository(dataStore: DataStore<Preferences>) {
    dataStoreInstance = dataStore
}

// Actual implementation for iOS
actual fun provideAuthRepository(): AuthRepository {
    val userAuthPreferencesRepository = UserAuthPreferencesRepository(dataStoreInstance)
    return createAuthRepository(userAuthPreferencesRepository)
}
